onMouseDrag:
var path = new Path.Circle({
                    center: event.downPoint,
                    radius: (event.downPoint - event.point).length
                    
                });
            if (colorFill()!=""){
                path.fillColor = colorFill();
                
                }
            if (colorStroke()!=""){
                path.strokeColor =  colorStroke();
            
                }
            path.removeOnDrag();
