onMouseDown:
tool.fixedDistance = 10;

path = new Path();
                path.strokeColor = colorFill();
                path.add(event.point);

onMouseDrag:
            path.add(event.point);

                var step = event.delta;
                step.angle += 90;
            
                var top = event.middlePoint + step;
                var bottom = event.middlePoint - step;
                
                var line = new Path();
                line.strokeColor = colorFill();
                line.add(top);
                line.add(bottom);
                 path.smooth();
